import React, { useEffect, useState } from 'react'
import { GifGridItem } from './GifGridItem';
import { getGif } from '../helpers/getGifs';

export const GifGrid = ({category}) => {

    const [Images, setImages] = useState([]);

    useEffect( () =>{
        getGif(category)
            .then(setImages)
    }, [category])

    return (
        <>
        <h3>{category}</h3>
        <div className="card-grid">
                {
                    Images.map( img => (
                        <GifGridItem 
                            key={img.id}
                            {...img}
                        />
                    ))
                }
        </div>
        </>
    )
}
